INSERT INTO `sao_mail_template` (`id_mail_template`, `name`, `subject`, `sender`, `text`, `html`, `wrap`, `deleted`, `version`, `version_created_at`, `version_created_by`)
  VALUES
  (1,'comment','{%profileName} is talking about your {%primitiveType}',NULL,'Your {%primitiveType} inspired {%profileName} to say:\n\n\"{%messageText}\"\n\n{%primitiveUrl}\n\nThanks,\nSaatchi Online\n\nThis message was intended for {%email}  If you do not wish to receive this type of email from Saatchi Online in the future, please click on the link below to unsubscribe.\n{%accountSettingsUrl}','Your {%primitiveType} inspired {%profileName} to say:<br/><br/>\"{%messageText}\"<br/><br/><a href=\"{%primitiveUrl}\">{%primitiveUrl}</a><br/><br/>Thanks,<br/>Saatchi Online<br/><br/>This message was intended for {%email}  If you do not wish to receive this type of email from Saatchi Online in the future, please click on the link below to unsubscribe.<br/><a href=\"{%accountSettingsUrl}\">{%accountSettingsUrl}</a>',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (2,'conversation','New message from {%regarding}',NULL,'Dear {%profileName},\r\n\r\nYou have a new message waiting from {%regarding}:\r\n\r\n{%msg}\r\n\r\nTo reply to this please cut and paste {%url} into your web browser.\r\n\r\nSincerely,\r\nSaatchi Online','<p>Dear {%profileName},<br />\r\n<br />\r\nYou have a new message waiting from {%regarding}:<br />\r\n<br />\r\n{%msg}<br />\r\n<br />\r\nTo reply to this please click <a href=\"{%url}\">{%url}</a>.<br />\r\n<br />\r\nSincerely,<br />\r\nSaatchi Online</p>\r\n',40,0,3,'2013-05-24 12:29:10','noAuth'),
  (3,'email-verification','Please verify your email address',NULL,'Hi {%profileName},\n\nThank you for signing up on Saatchi Online.\nIn order to perform certain actions, including uploading/selling your work and participating in Showdown, you must verify your email address by clicking the link below:\n\n{%messageUrl}\n\nRegards,\nSaatchi Online\n','Hi {%profileName},<br><br>\n\nThank you for signing up on Saatchi Online.\nIn order to perform certain actions, including uploading/selling your work and participating in Showdown, you must verify your email address by clicking the link below:<br><br>\n\n<a href=\"{%messageUrl}\" target=\"_blank\">Verify Email Address</a><br><br>\n\nIf you have problems with the link above, copy and paste this url into your browser: {%messageUrl}<br><br>\n\nRegards,<br><br>\nSaatchi Online<br><br>\n',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (4,'epic_fail','You placed second in Showdown.',NULL,'Dear {%profileName},\n\nYou placed second in Showdown. Your artwork will go on to be displayed at the Saatchi Gallery in London. A Saatchi Online staff member will be in touch with you shortly to discuss your prize.\n\nDid you know you can sell prints of this art on Saatchi Online? Click here http://www.saatchionline.com/guidelines to find out how.\n\nRegards,\nSaatchi Online','Dear {%profileName},<br/><br/>You placed second in Showdown. Your artwork will go on to be displayed at the Saatchi Gallery in London. A Saatchi Online staff member will be in touch with you shortly to discuss your prize.<br/><br/>Did you know you can sell prints of this art on Saatchi Online? <a href=\"http://www.saatchionline.com/guidelines\">Click here</a> to find out how.<br/><br/>Regards,<br/>Saatchi Online',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (5,'epic_win','You are our Showdown winner.',NULL,'Dear {%profileName},\n\nYou are the winner of Showdown! Your artwork beat out all the other entries and will go on to be displayed at the Saatchi Gallery in London. A Saatchi Online staff member will be in touch with you shortly to discuss your prize.\n\nDid you know you can sell prints of this art on Saatchi Online? Click here http://www.saatchionline.com/guidelines to find out how.\n\nRegards,\nSaatchi Online','Dear {%profileName},<br/><br/>You are the winner of Showdown! Your artwork beat out all the other entries and will go on to be displayed at the Saatchi Gallery in London. A Saatchi Online staff member will be in touch with you shortly to discuss your prize.<br/><br/>Did you know you can sell prints of this art on Saatchi Online? <a href=\"http://www.saatchionline.com/guidelines\">Click here</a> to find out how.<br/><br/>Regards,<br/>Saatchi Online',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (6,'fail','Sorry. You did not advance to the next round of Showdown.',NULL,'Dear {%profileName},\n\nYour artwork did not advance to the next round of Showdown. Thank you for participating and please continue to vote on the {%numberRemaining} artworks that did advance by clicking on the link below:\n\n{%messageUrl}\n\nDid you know you can sell prints of this art on Saatchi Online? Click here http://www.saatchionline.com/guidelines to find out how.\n\nRegards,\n\nSaatchi Online','Dear {%profileName},<br/><br/>Your artwork did not advance to the next round of Showdown. Thank you for participating and please continue to vote on the {%numberRemaining} artworks that did advance by clicking on the link below:<br/><br/><a href=\"{%messageUrl}\">{%messageUrl}</a><br/><br/>Did you know you can sell prints of this art on Saatchi Online? <a href=\"http://www.saatchionline.com/guidelines\">Click here</a> to find out how.<br/><br/>Regards,<br/><br/>Saatchi Online',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (7,'followed_by','{%profileName} is now following you on Saatchi Online.',NULL,'Well done! You have new fan that thinks you\'re pretty wonderful!  {%profileName} is now following you on Saatchi Online.\n\nThink they\'re pretty wonderful too?\n\nYou may follow {%profileName} back or add as a friend by clicking on the \"follow\" or \"friend\" icon on their profile.\n\nThanks,\nSaatchi Online\nThis message was intended for {%email}  If you do not wish to receive this type of email from Saatchi Online in the future, please click on the link below to unsubscribe.\n{%accountSettingsUrl}','Well done! You have new fan that thinks you\'re pretty wonderful!  {%profileName} is now following you on Saatchi Online.<p><img src=\"{%avatarUrl}\" /></p><br/><br/>Think they\'re pretty wonderful too?<br/><br/>You may follow {%profileName} back or add as a friend by clicking on the \"follow\" or \"friend\" icon on <a href=\"{%messageUrl}\">their profile</a>.<br/><br/>Thanks,<br/>Saatchi Online<br/><br/>This message was intended for {%email}  If you do not wish to receive this type of email from Saatchi Online in the future, please click on the link below to unsubscribe.<br/>{%accountSettingsUrl}',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (8,'friend','{%profileName} wants to be friends on Saatchi Online',NULL,'{%profileName} wants to be friends with you on Saatchi Online.  Click below to reply to this request.\n\n{%messageUrl}\n\nThanks,\nSaatchi Online\nThis message was intended for {%email}.  If you do not wish to receive this type of email from Saatchi Online in the future, please click on the link below to unsubscribe.\n{%accountSettingsUrl}','{%profileName} wants to be friends with you on Saatchi Online.  <a href=\"{%messageUrl}\">Click here</a> to reply to this request.<br/><br/>Thanks,<br/><br/>Saatchi Online<br/><br/>This message was intended for {%email}.  If you do not wish to receive this type of email from Saatchi Online in the future, please click on the link below to unsubscribe.<br/>{%accountSettingsUrl}',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (9,'friend_accepted','{%profileName} is now your friend on Saatchi Online',NULL,'{%profileName} is now your friend on Saatchi Online.  Click below to view their profile.\n\n{%messageUrl}\n\nThanks,\nSaatchi Online\nThis message was intended for {%email}.  If you do not wish to receive this type of email from Saatchi Online in the future, please click on the link below to unsubscribe.\n{%accountSettingsUrl}','{%profileName} is now your friend on Saatchi Online.  <a href=\"{%messageUrl}\">Click here</a> to view their profile.<br/><br/>Thanks,<br/><br/>Saatchi Online<br/><br/>This message was intended for {%email}  If you do not wish to receive this type of email from Saatchi Online in the future, please click on the link below to unsubscribe.<br/>{%accountSettingsUrl}',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (10,'handpicked_auto_response','Thank you for your Saatchi Online Handpicked inquiry',NULL,'{%salutation},\r\n\r\nThank you for your Saatchi Online inquiry about {%artTitle} ({%artUrl}).  We are happy to find out more about the availability of the work and will contact you directly as soon as we have that information.  Please note it can take several days for an artist to get back to us due to the time difference in various countries.\r\n\r\nShould you have any additional questions, feel free to email us at handpicked{%saatchionline}.com.\r\n\r\nThank you again for your interest.  We look forward to assisting you.\r\n\r\nBest,\r\nSaatchi Online\r\nhandpicked{%saatchionline}.com','<span style=\"color: #666; font-size: 16px; font-weight: bold;\">{%salutation},</span>\r\n\r\n<p>Thank you for your Saatchi Online inquiry about <a href=\"{%artUrl}\">{%artTitle}</a>.  We are happy to find out more about the availability of the work and will contact you directly as soon as we have that information. Please note it can take several days for an artist to get back to us due to the time difference in various countries.</p>\r\n\r\n<p>Should you have any additional questions, feel free to email us at <a href=\"mailto:handpicked{%saatchionline}.com\">handpicked{%saatchionline}.com</a>.</p>\r\n\r\n<p>Thank you again for your interest.  We look forward to assisting you.</p>\r\n\r\n<img src=\"{%handpickedLogo}\" alt=\"Handpicked By Saatchi Online\" style=\"float:right; margin-left: 10px; width: 150px; height: auto;\" align=\"right\"; />\r\n<p>Best,<br/>\r\nSaatchi Online</p>',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (11,'id_rejected','Your Saatchi Online Photo ID Upload was rejected',NULL,'Dear {%profileName},\n\nWe have reviewed the Identification you submitted and determined it is not a valid government issued form of identification for the following reason: \n\n{%msg}\n\nPlease go to https://www.saatchionline.com/accounts/billing and upload a new government issued form of identification such as a Driver\'s License or Passport, so we can verify your identity. \n\nSincerely,\nSaatchi Online','Dear {%profileName},<br/><br/>We have reviewed the Identification you submitted and determined it is not a valid government issued form of identification for the following reason: <br/><br/>{%msg}<br/><br/>Please go to <a href=\"https://www.saatchionline.com/accounts/billing\">https://www.saatchionline.com/accounts/billing</> and upload a new government issued form of identification such as a Driver\'s License or Passport, so we can verify your identity.<br/><br/>Sincerely,<br/>Saatchi Online',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (12,'id_verified','You are now a selling artist; please fill out a tax form! ',NULL,'Dear {%profileName},\r\n\r\nGood news! We have verified your government issued identification (ID) and you are now a selling artist on Saatchi Online.\r\n\r\nThank you for choosing to sell your work with us.\r\n\r\nSincerely,\r\n\r\nSaatchi Online','Dear {%profileName},<br/><br/>Good news! We have verified your government issued identification (ID) and you are now a selling artist on Saatchi Online.<br/><br/>Thank you for choosing to sell your work with us.<br/><br/>Sincerely,<br/><br/>Saatchi Online',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (13,'item_shipped','Your order item has been shipped',NULL,'Dear {%profileName}\n\nWe\'d like to let you know that we\'ve shipped your artwork. You can track the status of this order online by visiting your Orders at http://www.saatchionline.com/accounts/orders.\n\nThe following item has been shipped to you:\n\n{%artTitle}, found at {%artUrl}\n\n\nPlease send an email to {%replyEmail} if you have any questions about this item.\n\nSincerely,\nSaatchi Online','Dear {%profileName},<br/>\n<br/>\nWe\'d like to let you know that we\'ve shipped your artwork. Tracking number{%trackinNumberSuffix}: {%trackingNumberAnchors}<br/>\n<br/>\nYou can also track the status of this order online by visiting your Orders at http://www.saatchionline.com/accounts/orders.<br/>\n<br/>\nThe following item has been shipped to you:<br/>\n<a href=\"{%artUrl}\">{%artTitle}</a><br/>\n<br/>\nPlease send an email to {%replyEmail} if you have any questions about this item.<br/>\n<br/>\nSincerely,<br/>\n\nSaatchi Online',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (14,'message_sent','Hello {%firstName}, you have a new message.',NULL,'{%profileName} sent you a message:\n\nSubject: {%messageSubject}\n\n{%messageText}\n\nTo reply to this message, click on the link below:\n{%messageUrl}\n\nThanks,\n\nSaatchi Online\n\nThis message was intended for {%email}  If you do not wish to receive this type of email from Saatchi Online in the future, please click on the link below:\n{%accountSettingsUrl}','{%profileName} sent you a message:<br/><br/>Subject: {%messageSubject}<br/><br/>{%messageTextHtml}<br/><br/>To reply to this message, <a href=\"{%messageUrl}\">click here</a><br/><br/>Thanks,<br/><br/>Saatchi Online<br/><br/>This message was intended for {%email}  If you do not wish to receive this type of email from Saatchi Online in the future, please click on the link below:<br/><a href=\"{%accountSettingsUrl}\">{%accountSettingsUrl}</a>',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (15,'newsletter_guest_artist_auto_response','Welcome to Saatchi Online',NULL,'Welcome to Saatchi Online.  We\'re glad you\'ve signed up for our artist\'s newsletter and hope that it is helpful and informative \n\nIn it you\'ll find updates on developments at Saatchi Online, news about Showdown competitions (http://www.saatchionline.com/showdown), feature stories on artists (http://magazine.saatchionline.com), regular tips from our curators to help you maximize sales and lots more.\n\nOur mission remains the same today as it was in \'06 when Charles Saatchi launched the site.  Our goal is to provide artists a platform to show their work to a global audience outside of the traditional gallery structure in order to connect them directly to collectors, first time buyers, and everyone in between.  \n\nSincerely, \nThe Saatchi Online Team \n\nWant even more ways to keep in touch?  Like us on Facebook.  Follow us on Twitter, Tumblr and Pinterest.\n\nhttps://www.facebook.com/saatchionline\nhttps://twitter.com/saatchionline\nhttp://blog.saatchionline.com/\nhttp://pinterest.com/saatchionline/','\n<p>Welcome to Saatchi Online.  We\'re glad you\'ve signed up for our artist\'s newsletter and hope that it is helpful and informative.</p> \n\n<p>In it you\'ll find updates on developments at Saatchi Online, news about <a href=\"http://www.saatchionline.com/showdown\">Showdown</a> competitions, <a href=\"http://magazine.saatchionline.com\">feature stories</a> on artists, regular tips from our curators to help you maximize sales and lots more.</p> \n\n<p>Our mission remains the same today as it was in \'06 when Charles Saatchi launched the site.  Our goal is to provide artists a platform to show their work to a global audience outside of the traditional gallery structure in order to connect them directly to collectors, first time buyers, and everyone in between.</p>  \n\n<p>If you\'re not yet <a href=\"http://www.saatchionline.com/artschool\">selling your original works or prints</a>, we\'d love to have you as part of the Saatchi Online community.   Have questions along the way?  Email us at <a href=\"mailto:support{%saatchionline}.com\">support{%saatchionline}.com</a>.</p>\n\n<p>Sincerely,<br/>\nThe Saatchi Online Team</p> \n\n<p>Want even more ways to keep in touch?  Like us on <a href=\"https://www.facebook.com/saatchionline\">Facebook</a>.  Follow us on <a href=\"https://twitter.com/saatchionline\">Twitter</a>, <a href=\"http://blog.saatchionline.com\">Tumblr</a> and <a href=\"http://pinterest.com/saatchionline/\">Pinterest</a>.</p>',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (16,'newsletter_guest_collector_auto_response','Welcome to Saatchi Online',NULL,'Welcome to Saatchi Online.\n\nThank you for signing up for our newsletter. Now you can look forward to discovering emerging artists from around the world weekly. Always wanted to explore the art scene in London or Copenhagen? Now you can.\n\nhttp://www.saatchionline.com/buy-art/for-sale-United-Kingdom-London\nhttp://www.saatchionline.com/buy-art/for-sale-Denmark-Copenhagen\n\nLaunched in \'06 by Charles Saatchi, Saatchi Online was created to provide emerging artists a platform to show their work to a global audience outside of the traditional gallery structure. \n\nToday, our mission remains the same, helping artists develop sustainable careers by connecting them directly to collectors, first time buyers, and everyone in between.  \n\nStart discovering!  Enjoy {%couponDiscount} off your first purchase. Enter {%couponCode} at checkout.  Good till {%couponGoodUntil}.\n\nSincerely, \nThe Saatchi Online Team\n\nWant even more ways to discover emerging talent?  Like us on Facebook.  Follow us on Twitter, Tumblr and Pinterest.\n\nhttps://www.facebook.com/saatchionline\nhttps://twitter.com/saatchionline\nhttp://blog.saatchionline.com/\nhttp://pinterest.com/saatchionline/','\n<p>Welcome to Saatchi Online.</p>\n\n<p>Thank you for signing up for our newsletter.  Now you can look forward to discovering emerging artists from around the world weekly.  Always wanted to explore the art scene in <a href=\"http://www.saatchionline.com/buy-art/for-sale-United-Kingdom-London\" title=\"Browse Saatchi Online Art From London\">London</a> or <a href=\"http://www.saatchionline.com/buy-art/for-sale-Denmark-Copenhagen\" title=\"Browse Saatchi Online Art From Copenhagen\">Copenhagen</a>?  Now you can.</p>\n\n<p>Launched in \'06 by Charles Saatchi, Saatchi Online was created to provide emerging artists a platform to show their work to a global audience outside of the traditional gallery structure. </p>\n\n<p>Today, our mission remains the same, helping artists develop sustainable careers by connecting them directly to collectors, first time buyers, and everyone in between.</p>\n\n<p>Start discovering!  Enjoy <strong>{%couponDiscount} off</strong> your first purchase. Enter <strong>{%couponCode}</strong> at checkout.  Good till <strong>{%couponGoodUntil}</strong>.</p>\n\n<p>Sincerely,<br/>\nThe Saatchi Online Team</p>\n\n<p>Want even more ways to discover emerging talent?  Like us on <a href=\"https://www.facebook.com/saatchionline\">Facebook</a>.  Follow us on <a href=\"https://twitter.com/saatchionline\">Twitter</a>, <a href=\"http://blog.saatchionline.com/\">Tumblr</a> and <a href=\"http://pinterest.com/saatchionline/\">Pinterest</a>.</p>',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (17,'order_confirmation','Your Saatchi Online Order Confirmation - #{%increment_id}','orders@saatchionline.com','Dear {%replacements.profileName},\r\n\r\nThank you for buying art on Saatchi Online. Your order is currently being processed and details can be viewed here: {%replacements.orderUrl}.\r\n\r\nHere is a review of your order:\r\n\r\nYour order number is {%increment_id}.\r\n\r\nSub-Total: {%totals.subtotal_without_item_expenses}\r\nDiscount: {%totals.discount} \r\nShipping: {%totals.freight_costs}\r\nCustom Charges: {%totals.customs_and_duties}\r\nSales Tax: {%totals.state_tax_amount}\r\nOrder Total: {%totals.grand_total}\r\n\r\n\r\nWe will send you an email when your item(s) has shipped. If you have any questions regarding this purchase, please contact us at {%replacements.replyEmail}, and we will assist you shortly.\r\n\r\nShipping time frames can vary based as items are shipped from all over the world.  Original works of art can take up to 3 weeks to be received. Prints can take up to 2 weeks to be delivered. \r\n\r\nThank you for supporting our artists on Saatchi Online. \r\n\r\nSincerely,\r\nSaatchi Online','<p>Dear {%replacements.profileName},<br />\r\n<br />\r\nThank you for buying art on Saatchi Online. Your order is currently being processed and details can be viewed here: {%replacements.orderUrl}.<br />\r\n<br />\r\nHere is a review of your order:<br />\r\n<br />\r\nYour order number is {%increment_id}.<br />\r\n<br />\r\nSub-Total: {%totals.subtotal_without_item_expenses}<br />\r\nDiscount: {%totals.discount}<br />\r\nShipping: {%totals.freight_costs}<br />\r\nCustom Charges: {%totals.customs_and_duties}<br />\r\nSales Tax: {%totals.state_tax_amount}<br />\r\nOrder Total: {%totals.grand_total}<br />\r\n<br />\r\n<br />\r\nWe will send you an email when your item(s) has shipped. If you have any questions regarding this purchase, please contact us at {%replacements.replyEmail}, and we will assist you shortly.<br />\r\n<br />\r\nShipping time frames can vary based as items are shipped from all over the world. Original works of art can take up to 3 weeks to be received. Prints can take up to 2 weeks to be delivered.<br />\r\n<br />\r\nThank you for supporting our artists on Saatchi Online.<br />\r\n<br />\r\nSincerely,<br />\r\nSaatchi Online</p>\r\n',40,0,4,'2013-06-10 16:26:12','marco.rossdeutscher'),
  (18,'print_purchased','You just sold a print on Saatchi Online',NULL,'Dear {%profileName},\r\n\r\nCongratulations! You have just sold a print of {%artTitle}.  \r\n\r\nYour Payout will not be available until after artwork has been shipped and received by the collector (http://www.saatchionline.com/help#request-a-payout). \r\n\r\nPlease note that in order to start receiving payments from Saatchi Online, you must provide the appropriate signed tax forms which you can find at http://www.saatchionline.com/taxes/.\r\n\r\nSaatchi Online requires all artists to submit a signed tax form (W9 for US residents, W8-BEN for Non-US residents) prior to processing artist payouts.  Tax forms are not accepted without your signature. Non-US residents may also apply for an ITIN (Individual Txapayer Identification Number). Without an ITIN, we will be required to deduct a 30% US Witholding Tax.\r\n\r\nSubmit completed tax forms to us via email at support{%saatchionline}.com.\r\n\r\nThank you for being part of the Saatchi Online community.\r\n\r\nSincerely,\r\nThe Saatchi Online Team','Dear {%profileName},<br/>\r\n<br/>\r\nCongratulations! You have just sold a print of {%artTitle}.<br/>  \r\n<br/>\r\nYour Payout will not be available until after artwork has been shipped and received by the collector <a href=\"http://www.saatchionline.com/help#request-a-payout\">(http://www.saatchionline.com/help#request-a-payout)</a>.<br/> \r\n<br/>\r\nPlease note that in order to start receiving payments from Saatchi Online, you must provide the appropriate signed tax forms which you can find at <a href=\"http://www.saatchionline.com/taxes/\">http://www.saatchionline.com/taxes/</a>.<br/>\r\n<br/>\r\nSaatchi Online requires all artists to submit a signed tax form (W9 for US residents, W8-BEN for Non-US residents) prior to processing artist payouts.  Tax forms are not accepted without your signature. Non-US residents may also apply for an ITIN (Individual Txapayer Identification Number). Without an ITIN, we will be required to deduct a 30% US Witholding Tax.<br/>\r\n<br/>\r\nSubmit completed tax forms to us via email at support{%saatchionline}.com.<br/>\r\n<br/>\r\nThank you for being part of the Saatchi Online community.<br/>\r\n<br/>\r\nSincerely,<br/>\r\nThe Saatchi Online Team<br/>\r\n<br/>',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (19,'refund_admin','Customer needs refund',NULL,'Item failed for the following reason:\n\n{%msg} \n\nClick on the link or paste it into your browser to process the refund:\n\n{%url}','Item failed for the following reason:<br/><br/>{%htmlMsg}<br/><br/>Click on the link or paste it into your browser to process the refund:<br/><br/><a href=\"{%url}\">{%url}</a>',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (20,'remove_file','Your print was removed from being for sale',NULL,'Dear {%profileName},\n\nAfter unsuccessful requests for a suitable image in order to sell your print, we have removed your print for sale for all sizes that do not meet our criteria.  If you wish to upload a new file, please do so by clicking here - http://www.saatchionline.com/upload.\n\nIf you have any further questions, please send us an email at {%replyEmail}.\n\nThank you,\nSaatchi Online','Dear {%profileName},<br/><br/>After unsuccessful requests for a suitable image in order to sell your print, we have removed your print for sale for all sizes that do not meet our criteria.  If you wish to upload a new file, please do so by clicking here - http://www.saatchionline.com/upload.<br/><br/>If you have any further questions, please send us an email at {%replyEmail}.<br/><br/>Thank you,<br/>Saatchi Online',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (21,'remove_original','Important information about your artwork',NULL,'Dear {%profileName},\n\nYour original work of art, {%artTitle}, found at {%artUrl} was not approved for sale because Saatchi Online deemed the work as a violation of our Terms of Service.  The following is a list of items that cannot be sold on Saatchi Online:\n\n - Guns, weapons, knives and/or ammunition\n - Human and/or animal parts and remains\n - Drugs and/or drug paraphernalia\n - Military items\n - Hazardous materials\n - Mailing lists and personal information, including IDs and licenses\n - Alcohol\n - Stolen property\n\nIf you believe your artwork did not violate Saatchi Online\'s Terms of Service and was removed by error, please contact us at {%replyEmail}.\n\nSincerely,\nSaatchi Online','Dear {%profileName},<br/><br/>Your original work of art, {%artTitle}, found at <a href=\"{%artUrl}\">{%artUrl}</a> was not approved for sale because Saatchi Online deemed the work as a violation of our Terms of Service.  The following is a list of items that cannot be sold on Saatchi Online:<br/><br/> - Guns, weapons, knives and/or ammunition<br/> - Human and/or animal parts and remains<br/> - Drugs and/or drug paraphernalia<br/> - Military items<br/> - Hazardous materials<br/> - Mailing lists and personal information, including IDs and licenses<br/> - Alcohol<br/> - Stolen property<br/><br/>If you believe your artwork did not violate Saatchi Online\'s Terms of Service and was removed by error, please contact us at <a href=\"mailto:{%replyEmail}\">{%replyEmail}</a>.<br/><br/>Sincerely,<br/>Saatchi Online',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (22,'requestToSale','Someone is interested in buying your art',NULL,'Dear {%profileName},\n\nSomeone is interested in purchasing your work entitled \"{%artTitle}\" on Saatchi Online. If you would like to make the work for sale, please set the price by using this link {%artLink} and we will inform the person who inquired about it that\'s it\'s been listed for sale.\n\nEmail us at {%contact} if you have any questions.\n\nSincerely,\nSaatchi Online','Dear {%profileName},<br/>\n<br/>\nSomeone is interested in purchasing your work entitled \"{%artTitle}\" on Saatchi Online. If you would like to make the work for sale, please set the price by using this link <a href=\'{%artLink}\'>{%artLink}</a> and we will inform the person who inquired about it that\'s it\'s been listed for sale.\n<br/><br/>\nEmail us at <a href=\'{%contact}\'>{%contact}</a> if you have any questions.\n<br/><br/>\nSincerely,<br/>\nSaatchi Online',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (23,'reupload_file','You almost sold a print. Please re-upload your image',NULL,'Dear {%profileName},\n\nWe need a higher quality image before we can sell your print.  Please re-upload your image by clicking on this link:\n\n{%url}  \n\nYou will receive a confirmation email once we approve the image and sale.\n\nThank you,\nSaatchi Online','Dear {%profileName},<br/><br/>We need a higher quality image before we can sell your print.  Please re-upload your image by clicking on this link:<br/><br/><a href=\"{%url}\">{%url}</a><br/><br/>You will receive a confirmation email once we approve the image and sale.<br/><br/>Thank you,<br/>Saatchi Online',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (24,'ship_original','You sold a piece of original art on Saatchi Online',NULL,'Dear {%profileName}\r\n\r\nCongratulations!  Your artwork, {%artTitle}, {%artUrl}, has sold.  \r\n\r\nTo make shipment of your artwork easier for you, one of our Saatchi Online Customer team will be:\r\n\r\n- Scheduling a pick up. Please confirm or update the pick up address and pick up time. *see below*\r\n\r\n- A pickup confirmation, shipping label, and any necessary documents (e.g. commercial invoice) will be emailed to you.\r\n\r\nBefore we can help you ship your work, please reply to {%replyEmail} with the following information:\r\n\r\nDaytime Phone Number: {%artPhoneNumber}\r\nPickup Address:\r\n    Contact person:\r\n    Is this a business location, if yes, Company Name:\r\n    Is this a residence:\r\n    Location of package:\r\n    Address Line 1: {%artAddress}1\r\n    Address Line 2: {%artAddress}2\r\n    Address Line 3:\r\n    Postal/Zip Code: {%artPostalCode}\r\n    City: {%artCity}\r\n    Province/State/Region:{%artProvince}\r\n    Country: {%artCountry}\r\nShipping Weight:\r\nShipping Dimensions ( height x length x depth ):\r\nPreferred Pickup Time:  Choose a 4 hour time period between the hours of 9 am and 5 pm during which you are available.\r\nPreferred Pickup Date:  Choose a date ( Monday - Friday) between {%pickupDate}.\r\n\r\nPlease note that for certain shipments, you must be available between the hours of 8 am and 6 pm on the date of pickup.  We will let you know when we schedule the pickup if this applies to you.\r\n\r\nWe will schedule the pickup for you using our shipping account.  You are responsible for securely packaging the art.\r\n\r\nNeed tips on how to package your artwork? Go to http://www.saatchionline.com/packaging.\r\n\r\nYour sale proceeds will be made available to you 7 days after successful delivery to the collector in your Sales Dashboard under Account. Please make sure your tax forms have been filled out and sent to accounting{%saatchionline}.com to avoid any payout delays.\r\n\r\nThank you for selling your work on Saatchi Online.\r\n\r\nSincerely,\r\nSaatchi Online','Dear {%profileName},<br/>\r\n<br/>\r\nCongratulations!  Your artwork, {%artTitle} <a href=\"{%artUrl}\">{%artUrl}</a>, has sold.  \r\n<br/>\r\nTo make shipment of your artwork easier for you, one of our Saatchi Online Customer team will be:\r\n<br/>\r\n- Scheduling a pick up. Please confirm or update the pick up address and pick up time. *see below*\r\n<br/>\r\n- A pickup confirmation, shipping label, and any necessary documents (e.g. commercial invoice) will be emailed to you.\r\n<br/>\r\nBefore we can help you ship your work, please reply to <a href=\"mailto:{%replyEmail}\">{%replyEmail}</a> with the following information:<br/>\r\n<br/>\r\nDaytime Phone Number: {%artPhoneNumber}<br/>\r\nPickup Address:<br/>\r\n    Contact person:<br/>\r\n    Is this a business location, if yes, Company Name:<br/>\r\n    Is this a residence:<br/>\r\n    Location of package:<br/>\r\n    Address Line 1: {%artAddress}1<br/>\r\n    Address Line 2: {%artAddress}2<br/>\r\n    Address Line 3:<br/>\r\n    Postal/Zip Code: {%artPostalCode}<br/>\r\n    City: {%artCity}<br/>\r\n    Province/State/Region:{%artProvince}<br/>\r\n    Country:{%artCountry}<br/>\r\nShipping Weight:<br/>\r\nShipping Dimensions ( height x length x depth ):<br/>\r\nPreferred Pickup Time:  Choose a 4 hour time period between the hours of 9 am and 5 pm during which you are available.<br/>\r\nPreferred Pickup Date:  Choose a date (Monday - Friday) between {%pickupDate}.<br/>\r\n<br/>\r\nPlease note that for certain shipments, you must be available between the hours of 8 am and 6 pm on the date of pickup.  We will let you know when we schedule the pickup if this applies to you.<br/>\r\n<br/>\r\nWe will schedule the pickup for you using our shipping account.  You are responsible for securely packaging the art.<br/>\r\n<br/>\r\nNeed tips on how to package your artwork?  Click here <a href=\"http://www.saatchionline.com/packaging\">http://www.saatchionline.com/packaging</a>.<br/>\r\n<br/>\r\nYour sale proceeds will made available to you 7 days after successful delivery to the buyer in your Sales Dashboard under Account. Please make sure your tax forms have been filled out and sent to <a href=\"mailto:accounting{%saatchionline}.com\">accounting{%saatchionline}.com</a> to avoid any payout delays.\r\n<br/>\r\nThank you for selling your work on Saatchi Online.<br/>\r\n<br/>\r\nSincerely,<br/>\r\nSaatchi Online',40,0,1,'2013-05-24 09:17:05','noAuth'),
  (25,'showdown_advancing_first_round','Congratulations, you made it to the next round of Showdown',NULL,'{%firstName},\r\n\r\nWe are happy to let you know that you have advanced to the next round of the Saatchi Online Showdown and are among the {%bracketCutoff} remaining artists.\r\n\r\nThe next round will be voted on by a jury led by Rebecca Wilson, Director, Saatchi Gallery, London.\r\n\r\nThe final round ({%numCurated} works) will be voted on by {%judgeText}, who will select the 1st and 2nd place works.\r\n\r\nThank you again for being part of the Saatchi Online community, we greatly value your participation. Good luck!\r\n\r\nSincerely,\r\nThe Saatchi Online Team','<span style=\"color: #666; font-size: 16px; font-weight: bold;\">{%firstName},</span> \r\n\r\n<p>We are happy to let you know that you have advanced to the next round of the Saatchi Online Showdown and are among the {%bracketCutoff} remaining artists.</p>\r\n\r\n<p>The next round will be voted on by a jury led by Rebecca Wilson, Director, Saatchi Gallery, London.</p>\r\n\r\n<p>The final round ({%numCurated} works) will be voted on by {%judgeText}, who will select the 1st and 2nd place works.</p>\r\n\r\n<p>Thank you again for being part of the Saatchi Online community, we greatly value your participation. Good luck!</p>\r\n\r\n<p>  \r\nSincerely,<br> \r\nThe Saatchi Online Team\r\n</p>',NULL,0,1,'2013-05-24 09:17:05','noAuth'),
